// import FileInput from "./FileInput";
import PositionInput from "./PositionInput";
const UserForm = ({positions, onSubmit}) => {



    const handleSubmit = e => {
        e.preventDefault();
        const form = e.target;


        const user = {
            name: form.name.value,
            email: form.email.value,
            phone: form.phone.value,
            position: userPositionObj.position,
            position_id: userPositionObj.position_id,
        };


        onSubmit(user);
        form.reset();

      };

    return(
        <form onSubmit={handleSubmit}>
            <input 
                name="name" 
                type="name" 
                pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$" 
                autoComplete="off"
                placeholder="name"
                maxLength={60}
                minLength={2}
                required /> <br/>
            <input 
                name="email" 
                type="email" 
                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                autoComplete="off" 
                placeholder="email" 
                required />  <br/>

            <label>   
            <input 
                type="tel" 
                name="phone" 
                pattern="[+][3][8][(][0-9]{3}[)][0-9]{3}[-][0-9]{2}[-][0-9]{2}"
                autoComplete="off" 
                placeholder="phone" 
                required/><br/>

            <small>Format: +38(XXX)XXX-XX-XX</small>
            </label>

            <p>Select your position</p>
            <PositionInput positions={positions}/>

            {/* <label htmlFor="input-photo">
                <input type="file" name="photo" id="photoInput"/>
                Upload your photo
            </label>   <br /> */}
            {/* <FileInput /> */}


            <button type="submit">Sign In!</button>
        </form>
    )
}

export default UserForm;
