const UserForm = ({positions, onSubmit}) => {

    let userPositionObj = {};
    const setPosition = (e) => {
        userPositionObj = {
            position: e.target.value,
            position_id: e.target.id,
        }
    }

    const handleSubmit = e => {
        e.preventDefault();
        const form = e.target;

        const user = {
            name: form.name.value,
            email: form.email.value,
            phone: form.phone.value,
            position: userPositionObj.position,
            position_id: userPositionObj.position_id,
        };

        onSubmit(user);
        form.reset();

      };

    return(
        <form onSubmit={handleSubmit}>
            <input 
                name="name" 
                type="name" 
                pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$" 
                autoComplete="off"
                placeholder="name" 
                required /> <br/>
            <input 
                name="email" 
                type="email" 
                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                autoComplete="off" 
                placeholder="email" 
                required />  <br/>

            <label>   
            <input 
                type="text" 
                name="phone" 
                pattern="[+][3][8][0-9]{3}[0-9]{3}[0-9]{2}[0-9]{2}"
                autoComplete="off" 
                placeholder="phone" 
                required/><br/>

            <small>Format: +380990009900</small>
            </label>
            <p>Select your position</p>

            <div onChange={setPosition.bind()} required>
                {positions && positions.map(({id, name}) => (
                    <label key={id} htmlFor={id}>
                        <input type="radio" id={id} value={name} name='positions' required />
                        {name} <br />
                    </label> 
                ))}
            </div>

            <button type="submit">Sign In!</button>
        </form>
    )
}

export default UserForm;
