import Tooltip from "./Tooltip";

//images
import photoDefault from "../../images/photo-cover.jpg";

//components styled
import {
  Item,
  Avatar,
  // NameContainer,
  Position,
  // EmailContainer,
  Phone,
} from "./UserItem.styled";

const UserItem = ({ email, name, phone, photo, position }) => {
  return (
    <Item>
      <Avatar
        src={photo}
        alt="user-avatar"
        width="70px"
        height="70px"
        onError={({ currentTarget }) => {
          currentTarget.onError = null;
          currentTarget.src = photoDefault;
        }}
      />
      {/* <NameContainer> */}
      <Name>
        {name}
        <Tooltip text={name} />
      </Name>

      {/* </NameContainer> */}
      <Position>{position}</Position>
      {/* <EmailContainer> */}
      <p>{email}</p>
      <Tooltip text={email} />
      {/* </EmailContainer> */}
      <Phone>{phone}</Phone>
    </Item>
  );
};

export default UserItem;
