const UserForm = ({positions, onSubmit}) => {

    const handleSubmit = e => {
        e.preventDefault();
        const form = e.target;
        // const query = form.search.value;
        // setSearchParams({ query: query });
        console.log(form.name)
      };
    return(
        <form onSubmit={handleSubmit}>
            <input name="name" autoComplete="off" placeholder="name"></input><br/>
            <input name="email" autoComplete="off" placeholder="email"></input><br/>
            <label>   
            <input name="phone" autoComplete="off" placeholder="phone"></input><br/>
            +38(XXX)-XXX-XX-XX
            </label>
            <p>Select your position</p>
            <div>
                {positions && positions.map(({id, name}) => (
                    <label key={id} htmlFor={id}>
                        <input type="radio" id={id} value={name} name='positions'/>
                        {name} <br />
                    </label> 
                ))}
            </div>

            <button type="submit">Sign In!</button>
        </form>
    )
}

export default UserForm;
