import { useEffect, useState, useRef } from "react";
import { getNewToken, getUsers, getPosition, updateUsers } from "../../services/users-api";

import UserList from "../UsersList/UserList";

function App() {

  const [users, setUsers] = useState([]);
  const [token, setToken] = useState('');

  const isFirstRender = useRef(true);


  // useEffect(()=>{
  //   getUsers()
  //   .then(users => {setUsers(users)})
  //   .catch(error => console.log(error))}
  // ,[]);

  useEffect(() => {
    setToken(getNewToken());
    getUsers().then(users => {setUsers(users)}).catch(error => console.log(error));
  },[]);

  useEffect(() => {
    if(isFirstRender.current){
      isFirstRender.current = false;
      return;
    }
    getUsers().then(users => {setUsers(users)}).catch(error => console.log(error));


  },[users])


  const refresh = () => {
    getUsers()
    .then(users => {setUsers(users)})
    .catch(error => console.log(error))
  };

  return (
    <div>
      <h1>React app</h1>
      <button onClick={() => getNewToken()}>Get Token</button> <br/>
      <button onClick={refresh}>Get Users</button> <br />
      <button onClick={() => getPosition()}>Get Position</button>

      <UserList users={users}/>
    </div>
  );
}

export default App;
