import { Container, Label, Input } from "./UserPositions.styled";

const UserPositions = ({ positions, onChange }) => {
  return (
    <Container onChange={(id) => onChange(id)}>
      {positions &&
        positions.map(({ id, name }) => (
          <Label key={id} htmlFor={id}>
            <Input type="radio" id={id} value={name} name="position" required />
            <span>{name}</span>
          </Label>
        ))}
    </Container>
  );
};

export default UserPositions;
