//images
import photoDefault from "../../images/photo-cover.jpg";

//components styled
import {
  Item,
  Avatar,
  NameContainer,
  Position,
  EmailContainer,
  Phone,
  Tooltip,
} from "./UserItem.styled";

const UserItem = ({ email, name, phone, photo, position }) => {
  return (
    <Item>
      <Avatar
        src={photo}
        alt="user-avatar"
        width="70px"
        height="70px"
        onError={({ currentTarget }) => {
          currentTarget.onError = null;
          currentTarget.src = photoDefault;
        }}
      />
      <NameContainer>
        <p>{name}</p>
        {name.length > 60 && <Tooltip>{name}</Tooltip>}
      </NameContainer>
      <Position>{position}</Position>
      <EmailContainer>
        <p>{email}</p>
        {email.length > 60 && <Tooltip>{email}</Tooltip>}
      </EmailContainer>
      <Phone>{phone}</Phone>
    </Item>
  );
};

export default UserItem;
