import photoDefault from '../images/default-photo.jpg';

const BASE_URL = 'https://frontend-test-assignment-api.abz.agency/api/v1';
const axios = require('axios');

let token = '';
// let users = [];


async function getNewToken() {
    try{
        const response = await axios.get(
            `${BASE_URL}/token`
            );
            token = response.data.token;
            console.log(token);
            return token;
    } catch(error){
        return error;
    }
}

async function getUsers(page) {
    try {
        const response = await axios.get(
            `${BASE_URL}/users?page=${page}&count=6`
        )

        const users = response.data.users.map(({photo,email,id,title,phone,position,position_id,registration_timestamp}) => {
            const avatar = photo
              ? photo
              : photoDefault;
            return {
                email: email,
                id: id,
                name: title,
                phone: phone,
                position: position,
                photo: avatar,
                position_id: position_id,
                registration_timestamp: registration_timestamp,
            };
          });
        console.log(users)
        return users; 

    } catch(error){
        return error;
    }
} 

async function getPosition() {
    try{
        const response = await axios.get(
            `${BASE_URL}/positions`
        )
        const positions = response.data.positions;
        console.log(positions)
        return positions; 
    } catch(error){
        return error;
    }
}

async function updateUsers(user) {
    await axios.post(`${BASE_URL}/users`, {
        data: user,
      }).then(function(response) {
          console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
}

export {
    getNewToken,
    getUsers,
    getPosition,
    updateUsers,
}
