import { useState } from "react";


const UserForm = ({positions, onSubmit}) => {
    let position = null;

    const setPosition = (e) => {
        position = e.target.value;
    }

    const handleSubmit = e => {
        e.preventDefault();
        const form = e.target;
        // const query = form.search.value;
        // setSearchParams({ query: query });
        console.log(position);

        const user = {
            name: form.name.value,
            email: form.email.value,
            phone: form.phone.value,
            // position: form.positions.value.checked,
        };

        console.log(user);
      };
    return(
        <form onSubmit={handleSubmit}>
            <input name="name" autoComplete="off" placeholder="name"></input><br/>
            <input name="email" autoComplete="off" placeholder="email"></input><br/>
            <label>   
            <input name="phone" autoComplete="off" placeholder="phone"></input><br/>
            +38(XXX)-XXX-XX-XX
            </label>
            <p>Select your position</p>
            <div onChange={setPosition.bind(this)}>
                {positions && positions.map(({id, name}) => (
                    <label key={id} htmlFor={id}>
                        <input type="radio" id={id} value={name} name='positions'/>
                        {name} <br />
                    </label> 
                ))}
            </div>

            <button type="submit">Sign In!</button>
        </form>
    )
}

export default UserForm;
