import React, { useEffect } from 'react';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Toast = ({message}) => {
    const notify = (message) => toast({message});
    useEffect(() => {
        if (message === ''){
            return
        };
        notify(message)
    },[message])

  return (
    <div>
      <ToastContainer />
    </div>
  );
}

export default Toast;
