import styled from "@emotion/styled";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
`;
export const Label = styled.label`
  &:not(:last-of-type) {
    margin-bottom: 7px;
  }
`;
export const Input = styled.input`
  position: absolute;
  z-index: -1;
  opacity: 0;
`;
