import photoDefault from '../images/default-photo.jpg';

const BASE_URL = 'https://frontend-test-assignment-api.abz.agency/api/v1';
const axios = require('axios');
let token;
let pages;

async function getNewToken() {
    await axios.get(`${BASE_URL}/token`)
    .then(function(response) { 
        token = response.data.token;
        return token; 
    }) 
    .catch(function(error) { 
        console.log(error)
        return error;
    });

}

async function getUsers(page) {
    try {
        const response = await axios.get(
            `${BASE_URL}/users?page=${page}&count=6`
        )
        pages= response.data.total_pages; 
        const users = response.data.users.map(({photo,email,id,name,phone,position,position_id,registration_timestamp}) => {
            const avatar = photo
              ? photo
              : photoDefault;

            return {
                email: email,
                id: id,
                name: name,
                phone: phone,
                position: position,
                photo: avatar,
                position_id: position_id,
                registration_timestamp: registration_timestamp,
            };
          });

        return users; 

    } catch(error){
        return error;
    }
}

async function getPages(oldTotalPages){
    if(oldTotalPages === pages){
        console.log('oldpages: ',oldTotalPages)
        return oldTotalPages;
    } else {
        const response = await axios.get(
            `${BASE_URL}/users?page=1&count=6`
        )
        console.log('new-pages: ',response.data.total_pages)
        return response.data.total_pages; 
    }
}



async function getPositions() {
    try{
        const response = await axios.get(
            `${BASE_URL}/positions`
        )
        const positions = response.data.positions;

        return positions; 
    } catch(error){
        return error;
    }
}

async function addNewUser(userFormData) {
    await getNewToken();
    await axios({
        method: "post",
        url: `${BASE_URL}/users`,
        data: userFormData,
        headers: { "Token": token },
      })
        .then(function (response) {
        console.log(response.data);
            if(response.data.success){
                console.log('success')
            }else{
                console.log('server-error')
            }
        })
        .catch(function (response) {
            console.log('error response!')
            console.log(response);
        });
}


export {
    getNewToken,
    getUsers,
    getPositions,
    addNewUser,
    getPages
}
