import { useEffect, useState } from "react";
import { getUsers, addNewUser } from "../../services/users-api";
import { getPositions } from "../../services/users-api";


import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";
import toast, { Toaster } from 'react-hot-toast';

function App() {


  const [users, setUsers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(null);
  const [loading, setLoading] = useState(false);

  //useEffect для первого рендера
  useEffect(() => {
    setLoading(true)

    getPositions()
    .then(positions => {
      setPositions(positions);
      setLoading(false)
    })
    .catch(error => {
      console.log(error)
      setLoading(false)
    });

    setLoading(true);

    getUsers(1)
    .then(({users, pages}) => {
      setUsers(users);
      setPages(pages);
      setLoading(false);
    })
    .catch(error => {
      console.log(error);
      setLoading(false);
    });
  }, []);

  // useEffect для load more
  useEffect(() => {
    if (page === 1) {
      return;
    }
    console.log('page:', page, 'pages:', pages);

    getUsers(page)
    .then(({users, pages}) => {
      if(users.length > 0){
        setUsers(prevArr => [...prevArr, ...users])
      } 
      if (users.length === 0) {
        return Promise.reject(
          new Error(`Users not found.`)
        );

      }
      setPages(pages);
    })
    .catch(error => {
      console.log(error)
    }
    )
    .finally(() =>{
      setLoading(false);
    });
  }, [page])

  function loadMoreUsers () {
    setLoading(true);
    setPage(page + 1)
  }

  function refreshUsers(){
    getUsers(1)
      .then(({users, pages}) => { 
        console.log('users',users);
        setUsers(users) 
        setPages(pages)
      })
      .catch(error => console.log(error))
   setPage(1);
  };

  async function addUser({photo, name, email, phone, position_id}) {
    const userFormData = new FormData();
    userFormData.append('photo', photo);
    userFormData.append('name', name);
    userFormData.append('email', email);
    userFormData.append('phone', phone);
    userFormData.append('position_id', position_id);
    const statusAdding = await addNewUser(userFormData);

    if(statusAdding.succes === true){
      toast.success(statusAdding.message);
    } else {
      toast.error(statusAdding.message);
    }

    await refreshUsers();
  }


  const LOADMORE_VISIBLE = loading === false && page !== pages && users;
  const LOADMORE_LOADER = loading === true && page !== 1;
  const LOADING_USERS = loading === true && users.length === 0 && page === 1;
  const PENDING_USERS = users;

  return (
    <>
      <h1>React app</h1>
      <button onClick={refreshUsers}>Get Users</button> <br />
      {LOADING_USERS && <h2>Loading...</h2>}
      {PENDING_USERS && <UserList users={users}/>}

      {LOADMORE_LOADER && <h3>Loading...</h3>}
      {LOADMORE_VISIBLE && <button onClick={loadMoreUsers}>Load More</button>}

      <UserForm positions={positions} onSubmit={addUser}/>
      <Toaster/>
    </>
  );
}

export default App;
