import { useEffect, useState } from "react";
import { getUsers, addNewUser } from "../../services/users-api";
import { getPositions } from "../../services/users-api";

import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";

function App() {

  const [users, setUsers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(null);
  const [loading, setLoading] = useState(false);

  //useEffect для первого рендера
  useEffect(() => {
    setLoading(true)

    getPositions()
    .then(positions => {
      setPositions(positions);
      setLoading(false)
    })
    .catch(error => {
      console.log(error)
      setLoading(false)
    });

    setLoading(true);

    getUsers(1)
    .then(({users, pages}) => {
      setUsers(users);
      setPages(pages);
      setLoading(false);
    })
    .catch(error => {
      console.log(error);
      setLoading(false);
    });
  }, []);

  // useEffect для load more

  useEffect(() => {
    if (page === 1) {
      return;
    }
    console.log('page:', page, 'pages:', pages);

    getUsers(page)
    .then(({users, pages}) => {
      console.log(users);
      setLoading(false);
      if(users.length > 0){
        setUsers(prevArr => [...prevArr, ...users])
      } 
      if (users.length === 0) {
        return Promise.reject(
          new Error(`Movie by request: "${searchQuery.trim()}" - not found.`)
        );
      }
      setPages(pages);
    })
    .catch(error => {
      setLoading(false);
      console.log(error)
    }
    );
  }, [page])

  const loadMoreUsers = () => {
    setLoading(true);
    setPage(page + 1)
  }

  function refreshUsers(){
    setPage(1);
     getUsers(1)
      .then(({users, pages}) => { 
        setUsers(users) 
        setPages(pages)
      })
      .catch(error => console.log(error))

  };

  const addUser = ({photo, name, email, phone, position_id}) => {

    const userFormData = new FormData();

    userFormData.append('photo', photo);
    userFormData.append('name', name);
    userFormData.append('email', email);
    userFormData.append('phone', phone);
    userFormData.append('position_id', position_id);

    addNewUser(userFormData);

    refreshUsers();
  }

  // const LOADING_LoadMore = loading === true && users.length !== 0;
  // const LOADING_RefreshUsers = loading === true && users.length === 0;
  // const PENDING = loading === false;
  const LOADMORE = loading === false && page !== pages && users;

  return (
    <div>
      <h1>React app</h1>
      <button onClick={refreshUsers}>Get Users</button> <br />
      <UserList users={users}/>
      {LOADMORE && <button onClick={loadMoreUsers}>Load More</button>}

      {/* {PENDING && (
        <>
          <UserList users={users}/>
          {LOADMORE && <button onClick={loadMoreUsers}>Load More</button>}

        </>
      )}

      {LOADING_LoadMore && (
        <>
          <UserList users={users}/> 
          <h2>Loading...</h2>
        </>
      )}  */}

      <UserForm positions={positions} onSubmit={addUser}/>

    </div>
  );
}

export default App;
