import { useEffect, useState } from "react";
import { getUsers, addNewUser } from "../../services/users-api";
import { getPositions } from "../../services/users-api";

import notFoundImage from "../../images/not-found-image.jpg";


import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";
import toast, { Toaster } from 'react-hot-toast';

function App() {


  const [users, setUsers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(null);
  const [loading, setLoading] = useState(false);

  //useEffect для первого рендера
  useEffect(() => {
    setLoading(true)

    getPositions()
    .then(positions => {
      setPositions(positions);
      setLoading(false)
    })
    .catch(error => {
      console.log(error)
      setLoading(false)
    });

    setLoading(true);

    getUsers(1)
    .then(({users, pages}) => {
      setUsers(null);
      setPages(pages);
      setLoading(false);
    })
    .catch(error => {
      console.log(error);
      setLoading(false);
    });
  }, []);

  // useEffect для load more
  useEffect(() => {
    if (page === 1) {
      return;
    }
    console.log('page:', page, 'pages:', pages);

    getUsers(page)
    .then(({users, pages}) => {
      if(users.length > 0){
        setUsers(prevArr => [...prevArr, ...users])
      } 
      if (users.length === 0) {
        return Promise.reject(
          new Error(`Users not found.`)
        );

      }
      setPages(pages);
    })
    .catch(error => {
      console.log(error)
    }
    )
    .finally(() =>{
      setLoading(false);
    });
  }, [page])

  function loadMoreUsers () {
    setLoading(true);
    setPage(page + 1)
  }

  function refreshUsers(){
    getUsers(1)
      .then(({users, pages}) => { 
        console.log('users',users);
        setUsers(users) 
        setPages(pages)
      })
      .catch(error => console.log(error))
   setPage(1);
  };

  async function addUser({user, form}) {
    const userFormData = new FormData();
    userFormData.append('photo', user.photo);
    userFormData.append('name', user.name);
    userFormData.append('email', user.email);
    userFormData.append('phone', user.phone);
    userFormData.append('position_id', user.position_id);
    const statusAdding = await addNewUser(userFormData);
    console.log(statusAdding);
    if(statusAdding.success === true){
      toast.success(statusAdding.message);
      await refreshUsers();
      form.reset();
    } else {
      toast.error(statusAdding.message);
      await refreshUsers();
    }
  }



  const LOADMORE_VISIBLE = loading === false && page !== pages && users && users.length !== 0;
  const LOADMORE_LOADER = loading === true && page !== 1;
  const LOADING_USERS = loading === true && users.length === 0 && page === 1;
  const NOT_FOUND = loading === false && !users; 

  return (
    <>
      {NOT_FOUND && <img src={notFoundImage} alt="" />}


        {!NOT_FOUND && <a href="#sign-in-form">Sign In</a>}



        {LOADING_USERS && <h2>Loading...</h2>}
        <UserList users={users}/>


        {LOADMORE_LOADER && !NOT_FOUND && <h3>Loading...</h3>}
        {LOADMORE_VISIBLE && !NOT_FOUND && <button onClick={loadMoreUsers}>Load More</button>}

        <div>
          <h2>Sign In</h2>
          <a name="sign-in-form"></a>
          <UserForm id="sign-in-form" positions={positions} onSubmit={addUser}/>
        </div>
        <Toaster/>
    </>
  );
}

export default App;
