import photoDefault from '../images/default-photo.jpg';

const BASE_URL = 'https://frontend-test-assignment-api.abz.agency/api/v1';
const axios = require('axios');


async function getNewToken() {
    try{
        const response = await axios.get(
            `${BASE_URL}/token`
            );
            const token = response.data.token;
            return token;
    } catch(error){
        return error;
    }
}

async function getUsers(page) {
    try {
        const response = await axios.get(
            `${BASE_URL}/users?page=${page}&count=6`
        )

        const users = response.data.users.map(({photo,email,id,name,phone,position,position_id,registration_timestamp}) => {
            const avatar = photo
              ? photo
              : photoDefault;
            return {
                email: email,
                id: id,
                name: name,
                phone: phone,
                position: position,
                photo: avatar,
                position_id: position_id,
                registration_timestamp: registration_timestamp,
            };
          });

        return users; 

    } catch(error){
        return error;
    }
} 

async function getPositions() {
    try{
        const response = await axios.get(
            `${BASE_URL}/positions`
        )
        const positions = response.data.positions;

        return positions; 
    } catch(error){
        return error;
    }
}

async function addNewUser(userFormData) {
    const token = await getNewToken();
    await axios.post(`${BASE_URL}/users`, {
        data: userFormData,
        headers: { 'Token': token}
      }).then(function(response) {
          console.log(response);
      }).then(function(data){
          console.log(data);
          if(data.success){
              console.log('success')
          }else{
              console.log('server-error')
          }
      }).catch(function (error) {
        console.log(error);
      });
}

export {
    getNewToken,
    getUsers,
    getPositions,
    addNewUser,
}
