import { useState } from "react";
import UserPositions from "./UserPositions";

const UserForm = ({ positions, onSubmit }) => {
  const [photoFile, setPhotoFile] = useState(null);
  const [userPosition, setUserPosition] = useState({});
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);

  const handlePosition = (e) => {
    setUserPosition({
      position: e.target.value,
      position_id: e.target.id,
    });
  };

  const handlePhoto = (e) => {
    const uploadPhoto = e.target.files[0];
    if (!uploadPhoto) return;
    const oFReader = new FileReader();
    oFReader.readAsDataURL(uploadPhoto);
    oFReader.onload = (event) => {
      var image = new Image();
      image.src = event.target.result;
      image.onload = function () {
        if (
          image.width < 70 ||
          image.height < 70 ||
          uploadPhoto.size > 5120000
        ) {
          setError(true);
        } else {
          setPhotoFile(uploadPhoto);
          setError(false);
        }
      };
    };
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    const form = e.target;
    const user = {
      photo: photoFile,
      name: form.name.value,
      email: form.email.value,
      phone: form.phone.value,
      position_id: userPosition.position_id,
    };

    if (!error) {
      onSubmit({ user, form });
      setLoading(false);
    } else {
      console.log("Error signIn");
    }
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <label>
        <input
          name="name"
          type="name"
          pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
          autoComplete="off"
          placeholder="name"
          maxLength={60}
          minLength={2}
          required
        />
      </label>
      <br />
      <label>
        <input
          name="email"
          type="email"
          pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
          autoComplete="off"
          placeholder="email"
          required
        />
      </label>
      <br />
      <label>
        <input
          type="tel"
          name="phone"
          pattern="[+]{0,1}[3][8][0-9]{10}"
          // pattern="/[+]{0,1}380([0-9]{9})$)"
          max={13}
          maxLength={13}
          autoComplete="off"
          placeholder="phone"
          required
        />
        <br />
        <small>Format: +380990009900</small>
      </label>
      <p>Select your position</p>
      <UserPositions
        positions={positions}
        onChange={handlePosition.bind()}
        required
      />
      {error ? (
        <p>
          Warning! The photo must have a minimum resolution of 70x70 pixels and
          be no more than 5 MB.
        </p>
      ) : (
        <p>Upload your photo</p>
      )}
      <label htmlFor="input-photo">
        <input
          type="file"
          name="photo"
          accept=".jpg, .jpeg"
          onChange={handlePhoto}
        />
      </label>{" "}
      <br />
      <button type="submit" disabled={error}>
        {loading ? "Loading..." : "Sign In!"}
      </button>
    </form>
  );
};

export default UserForm;
