import { useEffect, useState, useRef } from "react";
import { getNewToken, getUsers } from "../../services/users-api";
import { getPositions } from "../../services/users-api";

import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";

function App() {

  const [users, setUsers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [token, setToken] = useState('');
  const [page, setPage] = useState(1);


//useEffect для первого рендера
  useEffect(() => {
      getNewToken().then(token => {
        setToken(token);
      }).catch(error => console.log(error));;

      getPositions().then(positions => {
        setPositions(positions);
      }).catch(error => console.log(error));

      getUsers(page).then(users => {setUsers(users)
      }).catch(error => console.log(error));

  },[]);

  useEffect(() => {
    if(page === 1){
      return;
    }
    getUsers(page).then(users => {
      setUsers(prevArr => [...prevArr, ...users])
    }).catch(error => console.log(error));

  },[page])


  const refreshUsers = () => {
    getUsers(1)
    .then(users => {setUsers(users)})
    .catch(error => console.log(error))
  };

  const loadMoreUsers = () => {
    setPage(page + 1);
  }

  const addUser = (user) => {
    console.log(user);
    setPage(1);
    refreshUsers();
  }

  return (
    <div>
      <h1>React app</h1>
      <button onClick={refreshUsers}>Get Users</button> <br />
      <UserList users={users}/>
      <button onClick={loadMoreUsers}>Load More</button>
      <UserForm positions={positions} onSubmit={addUser}/>
    </div>
  );
}

export default App;
