sourcesContent:
import styled from "@emotion/styled";

export const Item = styled.li`
  width: 328px;
  height: 254px;

  border-radius: 15px;
  background-color: var(--white-background-color);

  text-align: center;

  padding: 20px;

  margin: 0 auto;
  margin-bottom: 20px;
`;
export const Avatar = styled.img`
  display: inline-block;
  width: 70px;
  height: 70px;
  border-radius: 50%;
`;

export const Text = styled.p`
  display: block;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  width: 100%;
  padding-bottom: 20px;
  position: relative;
  cursor: pointer;

  &:hover {
    color: red;
  }

  &:hover > span {
    display: block;

    padding-left: 16px;
    padding-right: 16px;
    z-index: 5;
    white-space: pre-wrap;

    max-width: 280px;
    height: max-content;

    position: absolute;
    top: 20px;
    text-decoration: none;

    color: var(--white-text-color);
    background: rgba(0, 0, 0, 0.87);
    border-radius: 4px;
  }
`;

export const Position = styled.p``;

