//libs/hooks
import { useEffect, useState } from "react";

//components
import Loader from "../Loader/Loader";
import UserPositions from "./UserPositions/UserPositions";

//components styled
import {
  FormContainer,
  FormInput,
  NumberLabel,
  PositionsContainer,
  PositionsTitle,
  ImageInput,
  ButtonForm,
  TextInputsContainer,
  ImageContainer,
  ImageLabel,
  ImageInputContainer,
  ButtonContainer,
} from "./UsetForm.styled";

const UserForm = ({ positions, onSubmit }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [photoFile, setPhotoFile] = useState(null);
  const [userPositionId, setUserPositionId] = useState(null);

  const [emptyFields, setEmptyFields] = useState(true);
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (
      name.trim() === "" ||
      email.trim() === "" ||
      phone.trim() === "" ||
      userPositionId === null ||
      photoFile === null
    ) {
      setEmptyFields(true);
    } else {
      setEmptyFields(false);
    }
  }, [name, email, phone, userPositionId, photoFile]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case "name":
        setName(value);
        break;
      case "email":
        setEmail(value);
        break;
      case "phone":
        setPhone(value);
        break;
      default:
        return;
    }
  };

  const handlePosition = (e) => {
    setUserPositionId(e.target.id);
  };

  const handlePhoto = (e) => {
    const uploadPhoto = e.target.files[0];
    if (!uploadPhoto) {
      setPhotoFile(null);
      return;
    }
    const oFReader = new FileReader();
    oFReader.readAsDataURL(uploadPhoto);
    oFReader.onload = (event) => {
      var image = new Image();
      image.src = event.target.result;
      image.onload = function () {
        if (
          image.width < 70 ||
          image.height < 70 ||
          uploadPhoto.size > 5120000
        ) {
          setError(true);
        } else {
          setPhotoFile(uploadPhoto);
          setError(false);
        }
      };
    };
  };

  function resetForm() {
    setName("");
    setEmail("");
    setPhone("");
    setPhotoFile(null);
    setUserPositionId(null);
  }

  async function handleFormSubmit(e) {
    e.preventDefault();
    setLoading(true);
    const form = e.target;

    if (error === false || emptyFields === false) {
      const user = {
        photo: photoFile,
        name: form.name.value,
        email: form.email.value,
        phone: form.phone.value,
        position_id: userPositionId,
      };
      await onSubmit(user, resetForm)
        .then(() => {
          setLoading(false);
        })
        .catch(() => {
          setLoading(false);
        });
    }
  }

  const BUTTON_SIGNIN = loading === false && error === false;
  const LOADING_SIGNIN =
    loading === true && error === false && emptyFields === false;

  const EMPTY_PHOTO = photoFile === null ? true : false;
  const ERROR_PHOTO = error === true ? true : false;

  return (
    <FormContainer onSubmit={handleFormSubmit}>
      <TextInputsContainer>
        <FormInput
          onChange={handleChange}
          value={name}
          name="name"
          type="name"
          pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
          autoComplete="off"
          placeholder="Your name"
          maxLength={60}
          minLength={2}
          required
        />

        <FormInput
          onChange={handleChange}
          value={email}
          name="email"
          type="email"
          min={2}
          minLength={2}
          max={100}
          maxLength={100}
          pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
          autoComplete="off"
          placeholder="E-mail"
          required
        />

        <NumberLabel>
          <FormInput
            onChange={handleChange}
            value={phone}
            type="tel"
            name="phone"
            pattern="[+]{0,1}[3][8][0-9]{10}"
            // pattern="/[+]{0,1}380([0-9]{9})$)"
            max={13}
            maxLength={13}
            autoComplete="off"
            placeholder="Phone"
            required
          />
          <p>+38 (XXX) XXX - XX - XX</p>
        </NumberLabel>
      </TextInputsContainer>
      <PositionsContainer>
        <PositionsTitle>Select your position</PositionsTitle>
        <UserPositions
          positions={positions}
          onChange={handlePosition.bind()}
          required
        />
      </PositionsContainer>
      <div>
        <ImageContainer htmlFor="photo-file">
          <ImageInputContainer>
            <ImageInput
              type="file"
              name="photo"
              title="select"
              accept=".jpg, .jpeg"
              id="photo-file"
              onChange={handlePhoto}
            />
            <p>Upload</p>
          </ImageInputContainer>
          <ImageLabel>
            {!ERROR_PHOTO && photoFile !== null && photoFile.name}
            {EMPTY_PHOTO && !ERROR_PHOTO && "Select photo"}
            {ERROR_PHOTO &&
              "The photo must be <5 MB and resolution >70x70 pixels"}
          </ImageLabel>
        </ImageContainer>
      </div>
      <br />

      <ButtonContainer>
        {LOADING_SIGNIN && <Loader color="#00BDD3" />}
        {BUTTON_SIGNIN && (
          <ButtonForm
            type="submit"
            disabled={
              error === true || loading === true || emptyFields === true
            }
          >
            Sign Up
          </ButtonForm>
        )}
      </ButtonContainer>
    </FormContainer>
  );
};

export default UserForm;
