import { useState } from "react";

const UserForm = ({positions, onSubmit}) => {

    const [photoFile, setPhotoFile] = useState(null);
    const [userPosition, setUserPosition] = useState({});
    const [error, setError] = useState(false);


    const setPosition = (e) => {
        setUserPosition({
            position: e.target.value,
            position_id: e.target.id,
        })
    }

    // const checkPhoto = (photoFile) => {
    //     const reader = new FileReader();
    //     const photo = photoFile;
    //     reader.onload(function(){
    //         const image = new Image();
    //         image.onload = function() {
    //             console.log(this.width);
    //         }
    //     })
    // }

    const checkPhotoResolution = (checkPhoto) => {
        let width = null;
        let height = null;
        const oFReader = new FileReader();
        oFReader.readAsDataURL(checkPhoto);
        oFReader.onload = (event) => {
          var image = new Image();
          image.src = event.target.result;
          image.onload = function () {
            console.log(`width : ${image.width} px`, `height: ${image.height} px`);
            width = image.width;
            height = image.height;
            // console.log(width, height);
            // console.log(image)
            if(width < 70 || height < 70){
                return false;
            } else {
                return true;
            }
          };
        };


    }

    const checkPhotoSize = (checkPhoto) => {
        if(checkPhoto.size && checkPhoto.size > 5120000){
           return false
        } else {
          return true
        }
    }

    const photoSelectedHandler = (e) => {
        console.log(e.target.files[0]);
        const uploadPhoto = e.target.files[0];

        const photoValidRes = checkPhotoResolution(uploadPhoto);
        const photoValidSize = checkPhotoSize(uploadPhoto);

        if(photoValidRes && photoValidSize){
            setPhotoFile(uploadPhoto)
            setError(false);
        } else {
            setError(true);
        }
    }

    const handleSubmit = e => {
        e.preventDefault();
        const form = e.target;
        const user = {
            photo: photoFile,
            name: form.name.value,
            email: form.email.value,
            phone: form.phone.value,
            position_id: userPosition.position_id,
        };

        if (!error){
            onSubmit(user);
            // form.reset();
        } 

      };

    return(
        <form onSubmit={handleSubmit}>
            <input 
                name="name" 
                type="name" 
                pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$" 
                autoComplete="off"
                placeholder="name"
                maxLength={60}
                minLength={2}
                required /> <br/>
            <input 
                name="email" 
                type="email" 
                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                autoComplete="off" 
                placeholder="email" 
                required />  <br/>

            <label>

            <input 
                type="tel" 
                name="phone" 
                pattern="[+]{0,1}[3][8][0-9]{10}"
                // pattern="/[+]{0,1}380([0-9]{9})$)"
                max={13}
                maxLength={13}
                autoComplete="off" 
                placeholder="phone" 
                required/><br/>  
            <small>Format: +380990009900</small>

            </label>

            <p>Select your position</p>
            <div onChange={setPosition.bind()} required>
                {positions && positions.map(({id, name}) => (
                    <label key={id} htmlFor={id}>
                        <input type="radio" id={id} value={name} name='positions' required />
                        {name} <br />
                    </label> 
                ))}
            </div>

            {error ? <p>WARNING! Photo must be max 5Mb and min 70x70px</p> 
            : <p>Upload your photo</p> }

            <label htmlFor="input-photo">
                <input 
                type="file" 
                name="photo"
                accept=".jpg, .jpeg" 
                onChange={photoSelectedHandler}/>
            </label>   <br />

            <button type="submit" disabled={error}>Sign In!</button>
        </form>
    )
}

export default UserForm;
