//libs/hooks
import { useState } from "react";

//components
import Loader from "../Loader/Loader";
import UserPositions from "./UserPositions/UserPositions";

const UserForm = ({ positions, onSubmit }) => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [photoFile, setPhotoFile] = useState(null);
  const [userPosition, setUserPosition] = useState({});

  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleName = (e) => {
    if (e.target.value === "") return;
    setName(e.target.value);
    console.log(name);
  };
  const handleEmail = (e) => {};
  const handlePhone = (e) => {};

  const handleChange = (event) => {
    const { name, value } = event.target;

    switch (name) {
      case "name":
        setName(value);
        break;
      case "email":
        setEmail(value);
        break;
      case "phone":
        setPhone(value);
        break;
      default:
        return;
    }
  };

  const handlePosition = (e) => {
    setUserPosition({
      position: e.target.value,
      position_id: e.target.id,
    });
  };

  const handlePhoto = (e) => {
    const uploadPhoto = e.target.files[0];
    if (!uploadPhoto) return;
    const oFReader = new FileReader();
    oFReader.readAsDataURL(uploadPhoto);
    oFReader.onload = (event) => {
      var image = new Image();
      image.src = event.target.result;
      image.onload = function () {
        if (
          image.width < 70 ||
          image.height < 70 ||
          uploadPhoto.size > 5120000
        ) {
          setError(true);
        } else {
          setPhotoFile(uploadPhoto);
          setError(false);
        }
      };
    };
  };

  async function handleFormSubmit(e) {
    e.preventDefault();
    setLoading(true);
    console.log(loading);
    const form = e.target;
    const user = {
      photo: photoFile,
      name: form.name.value,
      email: form.email.value,
      phone: form.phone.value,
      position_id: userPosition.position_id,
    };

    if (error === false) {
      await onSubmit({ user, form });
      setLoading(false);
      console.log(loading);
    } else {
      setLoading(false);
      console.log(loading);
    }
  }

  // const SIGN_IN_BUTTON = loading === false && error === false;
  // const SIGN_IN_LOADING = loading === true && error === false;

  return (
    <form onSubmit={handleFormSubmit}>
      <input
        onChange={handleName}
        value={name}
        name="name"
        type="name"
        pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
        autoComplete="off"
        placeholder="Your name"
        maxLength={60}
        minLength={2}
        required
      />
      <br />
      <input
        onChange={handleEmail}
        value={email}
        name="email"
        type="email"
        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
        autoComplete="off"
        placeholder="E-mail"
        required
      />
      <br />
      <label>
        <input
          onChange={handlePhone}
          value={phone}
          type="tel"
          name="phone"
          pattern="[+]{0,1}[3][8][0-9]{10}"
          // pattern="/[+]{0,1}380([0-9]{9})$)"
          max={13}
          maxLength={13}
          autoComplete="off"
          placeholder="Phone"
          required
        />
        <br />
        <small>Format: +380990009900</small>
      </label>
      <p>Select your position</p>
      <UserPositions
        positions={positions}
        onChange={handlePosition.bind()}
        required
      />
      {error ? (
        <p>
          Warning! The photo must have a minimum resolution of 70x70 pixels and
          be no more than 5 MB.
        </p>
      ) : (
        <p>Upload your photo</p>
      )}
      <label htmlFor="input-photo">
        <input
          type="file"
          name="photo"
          accept=".jpg, .jpeg"
          onChange={handlePhoto}
          required
        />
      </label>{" "}
      <br />
      {loading === true && <Loader color="blue" />}
      {loading === false && (
        <button type="submit" disabled={loading === true}>
          Sign In
        </button>
      )}
      {/* {SIGN_IN_BUTTON && (
        <button type="submit" disabled={error === true || loading === true}>
          {"Sign In!"}
        </button>
      )}
      {SIGN_IN_LOADING && <Loader color="blue" />} */}
    </form>
  );
};

export default UserForm;
