import styled from "@emotion/styled";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
`;
export const Label = styled.label`
  height: 26px;

  &:not(:last-of-type) {
    margin-bottom: 7px;
  }

  & > input {
    position: absolute;
    z-index: -1;
    opacity: 0;
  }
  & > span {
    display: inline-flex;
    align-items: center;
    user-select: none;
  }

  & > span::before {
    content: "";
    display: inline-block;

    flex-shrink: 0;
    flex-grow: 0;
    border: 1px solid #adb5bd;
    border-radius: 50%;
    margin-right: 12px;
    background-repeat: no-repeat;
    background-position: center center;
    background-size: 50% 50%;

    width: 20px;
    height: 20px;
  }

  & > input:not(:disabled):not(:checked) + span:hover::before {
    border-color: var(--secondary-color);
  }

  /* & > input:not(:disabled):active + span::before {
    background-color: #b3d7ff;
    border-color: #b3d7ff;
  } */

  /* стили для радиокнопки, находящейся в фокусе */
  /* & > input:focus + span::before {
    box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
  } */

  /* стили для радиокнопки, находящейся в фокусе и не находящейся в состоянии checked */
  /* & > input:focus:not(:checked) + span::before {
    border-color: #80bdff;
  } */

  /* стили для радиокнопки, находящейся в состоянии checked */
  & > input:checked + span::before {
    border-color: var(--secondary-color);
    content: url("../../../images/checked-center.svg");
    width: 10px;
    height: 10px;
    /* background-image: url("../../../images/checked-center.svg"); */
  }

  /* стили для радиокнопки, находящейся в состоянии disabled */
  & > input:disabled + span::before {
    border-color: var(--border-color);
  }
`;
export const Input = styled.input`
  width: 20px;
  height: 20px;
`;
