//libs/hooks
import { useEffect, useState } from "react";
import toast, { Toaster } from "react-hot-toast";

//Api-service functions
import { getUsers, addNewUser } from "../../services/users-api";
import { getPositions } from "../../services/users-api";

//components
import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";
import Loader from "../Loader/Loader";
import NotFound from "../NotFound/NotFound";
import Header from "../Header/Header";

//styled components

function App() {
  const [users, setUsers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(null);
  const [loading, setLoading] = useState(false);

  //useEffect для первого рендера
  useEffect(() => {
    setLoading(true);

    getPositions()
      .then((positions) => {
        setPositions(positions);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });

    setLoading(true);

    getUsers(1)
      .then(({ users, pages }) => {
        setUsers(users);
        setPages(pages);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });
  }, []);

  // useEffect для load more
  useEffect(() => {
    if (page === 1) {
      return;
    }
    console.log("page:", page, "pages:", pages);

    getUsers(page)
      .then(({ users, pages }) => {
        if (users.length > 0) {
          setUsers((prevArr) => [...prevArr, ...users]);
        }
        if (users.length === 0) {
          return Promise.reject(new Error(`Users not found.`));
        }
        setPages(pages);
      })
      .catch((error) => {
        console.log(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [page]);

  function loadMoreUsers() {
    setLoading(true);
    setPage(page + 1);
  }

  function refreshUsers() {
    getUsers(1)
      .then(({ users, pages }) => {
        console.log("users", users);
        setUsers(users);
        setPages(pages);
      })
      .catch((error) => console.log(error));
    setPage(1);
  }

  async function addUser({ photo, name, email, phone, position_id }) {
    const userFormData = new FormData();
    userFormData.append("photo", photo);
    userFormData.append("name", name);
    userFormData.append("email", email);
    userFormData.append("phone", phone);
    userFormData.append("position_id", position_id);

    const statusAdding = await addNewUser(userFormData);
    console.log(statusAdding);
    if (statusAdding.success === true) {
      toast.success(statusAdding.message);
      await refreshUsers();
      setLoading(false);
    }
    if (statusAdding.success === false) {
      if (statusAdding.fails.phone) {
        toast.error(statusAdding.fails.phone[0]);
      } else {
        toast.error(statusAdding.message);
      }

      setLoading(false);
      // await refreshUsers();
    }
  }

  const LOADMORE_VISIBLE =
    loading === false && page !== pages && users && users.length !== 0;
  const LOADMORE_LOADER = loading === true && page !== 1;
  const LOADING_USERS =
    loading === true && users && users.length === 0 && page === 1;
  const NOT_FOUND = loading === false && !users;

  return (
    <>
      {NOT_FOUND && <NotFound />}

      {!NOT_FOUND && <Header />}

      {LOADING_USERS && <Loader color="blue" />}
      <h2>
        <a id="users-list"></a>Working with GET request
      </h2>
      <UserList users={users} loading={loading} />

      {LOADMORE_LOADER && !NOT_FOUND && <Loader color="blue" />}
      {LOADMORE_VISIBLE && !NOT_FOUND && (
        <button onClick={loadMoreUsers}>Load More</button>
      )}

      {!NOT_FOUND && (
        <div>
          <h2>
            <a name="sign-in-form"></a>Working with POST request
          </h2>

          <UserForm
            id="sign-in-form"
            positions={positions}
            onSubmit={addUser}
          />
        </div>
      )}
      <Toaster />
    </>
  );
}

export default App;
