import { useState } from "react";

const UserForm = ({ positions, onSubmit }) => {
  const [photoFile, setPhotoFile] = useState(null);
  const [userPosition, setUserPosition] = useState({});
  const [error, setError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const setPosition = (e) => {
    setUserPosition({
      position: e.target.value,
      position_id: e.target.id,
    });
  };

  // const checkPhoto = (photoFile) => {
  //     const reader = new FileReader();
  //     const photo = photoFile;
  //     reader.onload(function(){
  //         const image = new Image();
  //         image.onload = function() {
  //             console.log(this.width);
  //         }
  //     })
  // }

  async function checkPhotoResolution(uploadPhoto) {
    const oFReader = new FileReader();
    // if(!checkPhoto) {return};
    oFReader.readAsDataURL(uploadPhoto);
    oFReader.onload = (event) => {
      var image = new Image();
      image.src = event.target.result;
      image.onload = function () {
        // console.log(`width : ${image.width} px`, `height: ${image.height} px`);
        return { width: image.width, height: image.height };
      };
    };
  }

  async function checkPhotoSize(checkPhoto) {
    if (checkPhoto.size && checkPhoto.size > 5120000) {
      console.log("error: size > 5Mb");
      return false;
    } else {
      return true;
    }
  }

  const photoSelectedHandler = (e) => {
    console.log(e.target.files[0]);
    const uploadPhoto = e.target.files[0];
    if (!uploadPhoto) return;
    const oFReader = new FileReader();
    oFReader.readAsDataURL(uploadPhoto);
    oFReader.onload = (event) => {
      var image = new Image();
      image.src = event.target.result;
      image.onload = function () {
        if (
          image.width < 70 ||
          image.height < 70 ||
          uploadPhoto.size > 5120000
        ) {
          console.log("Error");
          setError(true);
        } else {
          setPhotoFile(uploadPhoto);
          setError(false);
          image.width < 70 || image.height < 70
            ? setErrorMessage(
                "The minimum resolution for a photo is 70x70 pixels"
              )
            : setErrorMessage("Maximum photo size - 5 Mb");
        }
        console.log(errorMessage);
      };
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const form = e.target;
    const user = {
      photo: photoFile,
      name: form.name.value,
      email: form.email.value,
      phone: form.phone.value,
      position_id: userPosition.position_id,
    };

    if (!error) {
      onSubmit(user);
      // form.reset();
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        name="name"
        type="name"
        pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
        autoComplete="off"
        placeholder="name"
        maxLength={60}
        minLength={2}
        required
      />{" "}
      <br />
      <input
        name="email"
        type="email"
        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
        autoComplete="off"
        placeholder="email"
        required
      />{" "}
      <br />
      <label>
        <input
          type="tel"
          name="phone"
          pattern="[+]{0,1}[3][8][0-9]{10}"
          // pattern="/[+]{0,1}380([0-9]{9})$)"
          max={13}
          maxLength={13}
          autoComplete="off"
          placeholder="phone"
          required
        />
        <br />
        <small>Format: +380990009900</small>
      </label>
      <p>Select your position</p>
      <div onChange={setPosition.bind()} required>
        {positions &&
          positions.map(({ id, name }) => (
            <label key={id} htmlFor={id}>
              <input
                type="radio"
                id={id}
                value={name}
                name="positions"
                required
              />
              {name} <br />
            </label>
          ))}
      </div>
      {error ? <p>{errorMessage}</p> : <p>Upload your photo</p>}
      <label htmlFor="input-photo">
        <input
          type="file"
          name="photo"
          accept=".jpg, .jpeg"
          onChange={photoSelectedHandler}
        />
      </label>{" "}
      <br />
      <button type="submit" disabled={error}>
        Sign In!
      </button>
    </form>
  );
};

export default UserForm;
