import { useEffect, useState, useRef } from "react";
import { getNewToken, getUsers } from "../../services/users-api";

import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";

function App() {

  const [users, setUsers] = useState([]);
  const [token, setToken] = useState('');
  const [page, setPage] = useState(1);

  const isFirstRender = useRef(true);


  useEffect(() => {
    if(isFirstRender.current){
      setToken(getNewToken());
      getUsers(page).then(users => {setUsers(users)
      }).catch(error => console.log(error));
    }
  });

  useEffect(() => {
    if(page === 1){
      return;
    }
    // setImagesArray(prevArr => [...prevArr, ...newImagesArray]);
    getUsers(page).then(users => {
      setUsers(prevArr => [...prevArr, ...users])
    }).catch(error => console.log(error));

  },[page])


  const refresh = () => {
    getUsers(1)
    .then(users => {setUsers(users)})
    .catch(error => console.log(error))
  };

  const loadMore = () => {
    setPage(page + 1);
    console.log(page);
  }

  return (
    <div>
      <h1>React app</h1>
      <button onClick={() => getNewToken()}>Get Token</button> <br/>
      <button onClick={refresh}>Get Users</button> <br />
      {/* <button onClick={() => getPositions()}>Get Position</button> */}

      <UserList users={users}/>
      <button onClick={loadMore}>Load More</button>
      <UserForm />
    </div>
  );
}

export default App;
