import photoDefault from '../images/default-photo.jpg';

const BASE_URL = 'https://frontend-test-assignment-api.abz.agency/api/v1';
const axios = require('axios');

let token = '';
// let users = [];


async function getNewToken() {
    try{
        const response = await axios.get(
            `${BASE_URL}/token`
            );
            token = response.data.token;
            console.log(token);
            return token;
    } catch(error){
        return error;
    }
}

async function getUsers(page) {
    try {
        const response = await axios.get(
            `${BASE_URL}/users?page=${page}&count=6`
        )
        // const users = response.data.users;
        const users = response.data.users.map(user => {
            const avatar = user.photo
              ? user.photo
              : photoDefault;
            return {
             email: user.email,
              id: user.id,
            name: user.title,
            phone: user.phone,
            position: user.position,
              photo: avatar,
              position_id: user.position_id,
registration_timestamp: user.registration_timestamp,
            };
          });
        console.log(users)
        return users; 

    } catch(error){
        return error;
    }
} 

// https://frontend-test-assignment-api.abz.agency/images/users/5fa2a6597d98216.jpeg


async function getPosition() {
    try{
        const response = await axios.get(
            `${BASE_URL}/positions`
        )
        const positions = response.data.positions;
        console.log(positions)
        return positions; 
    } catch(error){
        return error;
    }
}

async function updateUsers(user) {
    await axios.post(`${BASE_URL}/users`, {
        data: user,
      }).then(function(response) {
          console.log(response);
      }).catch(function (error) {
        console.log(error);
      });
}

export {
    getNewToken,
    getUsers,
    getPosition,
    updateUsers,
}
