import { useEffect, useState } from "react";
import { getNewToken, getUsers, addNewUser } from "../../services/users-api";
import { getPositions } from "../../services/users-api";

import UserList from "../UsersList/UserList";
import UserForm from "../UserForm/UserForm";

function App() {

  const [users, setUsers] = useState([]);
  const [positions, setPositions] = useState([]);
  const [token, setToken] = useState('');
  const [page, setPage] = useState(1);


  const [user, setUser] = useState();


//useEffect для первого рендера
  useEffect(() => {
      getNewToken().then(token => {
        setToken(token);
      }).catch(error => console.log(error));;

      getPositions().then(positions => {
        setPositions(positions);
      }).catch(error => console.log(error));

      getUsers(page).then(users => {setUsers(users)
      }).catch(error => console.log(error));

  },[]);


  //useEffect для load more
  useEffect(() => {
    if(page === 1){
      return;
    }
    getUsers(page).then(users => {
      setUsers(prevArr => [...prevArr, ...users])
    }).catch(error => console.log(error));

  },[page])


  const refreshUsers = () => {
    getUsers(1)
    .then(users => {setUsers(users)})
    .catch(error => console.log(error))
  };

  const loadMoreUsers = () => {
    setPage(page + 1);
  }

  const addUser = (user) => {
    // addNewUser(user)
    setUser(user);
    console.log(user);
    setPage(1);
    refreshUsers();
  }

  return (
    <div>
      <h1>React app</h1>
      <button onClick={refreshUsers}>Get Users</button> <br />
      <UserList users={users}/>
      <button onClick={loadMoreUsers}>Load More</button>
      <UserForm positions={positions} onSubmit={addUser}/>

      {user &&
        <div>
          <image src={user.photo} alt="user"/> 
          <p>{user.name}</p>
          <p>{user.email}</p>
          <p>{user.phone}</p>
          <p>{user.position}</p>
        </div>
      }

    </div>
  );
}

export default App;
