import styled from "@emotion/styled";

export const Container = styled.div`
  display: flex;
  flex-direction: column;
`;
export const Label = styled.label`
  &:not(:last-of-type) {
    margin-bottom: 7px;
  }

  & > input {
    position: absolute;
    z-index: -1;
    opacity: 0;
  }
  & > span {
    display: inline-flex;
    align-items: center;
    user-select: none;
  }
`;
export const Input = styled.input`
  &::before {
    color: red;
  }
`;
