import { useState } from "react";


const UserForm = ({positions, onSubmit}) => {
    const phoneInput = document.querySelector("#phonenumber")


// Проверяем фокус
phoneInput.addEventListener('focus', _ => {
    // Если там ничего нет или есть, но левое
    if(!/^\+\d*$/.test(phoneInput.value))
      // То вставляем знак плюса как значение
      phoneInput.value = '+';
  });

  phoneInput.addEventListener('keypress', e => {
    // Отменяем ввод не цифр
    if(!/\d/.test(e.key))
      e.preventDefault();
  });    


    let userPositionObj = {};
    const setPosition = (e) => {
        userPositionObj = {
            position: e.target.value,
            position_id: e.target.id,
        }
    }

    const handleSubmit = e => {
        e.preventDefault();
        const form = e.target;

        const user = {
            name: form.name.value,
            email: form.email.value,
            phone: form.phone.value,
            position: userPositionObj.position,
            position_id: userPositionObj.position_id,
        };

        onSubmit(user);
        form.reset();

      };
    return(
        <form onSubmit={handleSubmit}>
            <input 
                name="name" 
                type="name" 
                pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$" 
                autoComplete="off"
                placeholder="name" 
                required /> <br/>
            <input 
                name="email" 
                type="email" 
                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                autoComplete="off" 
                placeholder="email" 
                required />  <br/>
            <label>   
            <input 
            type="number" 
            name="phone" 
            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]"
            autoComplete="off" 
            placeholder="phone" 
            id="phonenumber"
            required/><br/>

            +38(XXX)-XXX-XX-XX
            </label>
            <p>Select your position</p>
            <div onChange={setPosition.bind(this)} required>
                {positions && positions.map(({id, name}) => (
                    <label key={id} htmlFor={id}>
                        <input type="radio" id={id} value={name} name='positions' required />
                        {name} <br />
                    </label> 
                ))}
            </div>

            <button type="submit">Sign In!</button>
        </form>
    )
}

export default UserForm;
